import React, { useEffect, useState } from "react";
import axios from "axios";

const MyApplications = () => {
  const [applications, setApplications] = useState([]);

  useEffect(() => {
    const fetchApplications = async () => {
      try {
        const token = localStorage.getItem("token"); // ⬅️ JWT should be stored after login
        const res = await axios.get("http://localhost:8000/applications/", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setApplications(res.data);
      } catch (error) {
        console.error("Failed to fetch applications", error);
      }
    };

    fetchApplications();
  }, []);

  return (
    <div>
      <h2>My Applications</h2>
      {applications.length === 0 ? (
        <p>No applications submitted yet.</p>
      ) : (
        applications.map((app) => (
          <div key={app.id} style={{ border: "1px solid #ccc", marginBottom: "10px", padding: "10px" }}>
            <p><strong>Job Title:</strong> {app.job_title}</p>
            <p><strong>Status:</strong> {app.status}</p>
            <p><strong>Applied on:</strong> {new Date(app.created_at).toLocaleString()}</p>
          </div>
        ))
      )}
    </div>
  );
};

export default MyApplications;
