import React, { useEffect, useState } from "react";
import API from "../api";
import { useNavigate } from "react-router-dom";

function AdminDashboard() {
  const [jobs, setJobs] = useState([]);
  const [applications, setApplications] = useState([]);
  const [showApplications, setShowApplications] = useState(false); // âœ… toggle state
  const [form, setForm] = useState({
    title: "",
    description: "",
    location: "",
    salary: "",
    status: "Active",
  });
  const [editId, setEditId] = useState(null);
  const navigate = useNavigate();

  const fetchJobs = async () => {
    try {
      const res = await API.get("/jobs/");
      setJobs(res.data);
    } catch (err) {
      if (err.response?.status === 401) {
        navigate("/login");
      }
    }
  };

  const fetchApplications = async () => {
    try {
      const res = await API.get("/applications/");
      setApplications(res.data);
    } catch (err) {
      console.error("Failed to fetch applications:", err);
    }
  };

  useEffect(() => {
    fetchJobs();
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const payload = {
      ...form,
      salary: parseFloat(form.salary),
    };

    try {
      if (editId) {
        await API.put(`/jobs/${editId}/`, payload);
        setEditId(null);
      } else {
        await API.post("/jobs/", payload);
      }
      setForm({ title: "", description: "", location: "", salary: "", status: "Active" });
      fetchJobs();
    } catch (err) {
      alert(editId ? "Failed to update job" : "Failed to add job");
    }
  };

  const handleDelete = async (id) => {
    try {
      await API.delete(`/jobs/${id}`);
      fetchJobs();
    } catch (err) {
      alert("Failed to delete job");
    }
  };

  const handleEdit = (job) => {
    setEditId(job.id);
    setForm({
      title: job.title,
      description: job.description,
      location: job.location,
      salary: job.salary,
      status: job.status,
    });
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const handleCancelEdit = () => {
    setEditId(null);
    setForm({ title: "", description: "", location: "", salary: "", status: "Active" });
  };

  const toggleApplications = async () => {
    if (!showApplications) {
      await fetchApplications();
    }
    setShowApplications(!showApplications);
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h2>Admin Dashboard</h2>

      <h3>{editId ? "Edit Job" : "Add Job"}</h3>
      <form onSubmit={handleSubmit} style={{ maxWidth: 600 }}>
        <input name="title" value={form.title} onChange={handleChange} placeholder="Title" required />
        <br />
        <textarea name="description" value={form.description} onChange={handleChange} placeholder="Description" required />
        <br />
        <input name="location" value={form.location} onChange={handleChange} placeholder="Location" required />
        <br />
        <input type="number" name="salary" value={form.salary} onChange={handleChange} placeholder="Salary" required />
        <br />
        <select name="status" value={form.status} onChange={handleChange}>
          <option value="Active">Active</option>
          <option value="Closed">Closed</option>
        </select>
        <br />
        <button type="submit">{editId ? "Update Job" : "Add Job"}</button>
        {editId && <button type="button" onClick={handleCancelEdit}>Cancel</button>}
      </form>

      <h3>All Jobs</h3>
      {jobs.map((job) => (
        <div key={job.id} style={{ margin: "1rem 0", padding: "1rem", border: "1px solid gray" }}>
          <h4>{job.title}</h4>
          <p>{job.location} | â‚¹{job.salary}</p>
          <p>{job.description}</p>
          <p>Status: {job.status}</p>
          <button onClick={() => handleEdit(job)}>Edit</button>
          <button onClick={() => handleDelete(job.id)} style={{ marginLeft: "1rem" }}>Delete</button>
        </div>
      ))}

      {/* Toggle Applications */}
      <div style={{ marginTop: "2rem" }}>
        <button onClick={toggleApplications}>
          {showApplications ? "Hide Applications" : "View Applications"}
        </button>
      </div>

      {/* Applications Table */}
      {showApplications && (
        <>
          <h3 style={{ marginTop: "1rem" }}>User Applications</h3>
          {applications.length === 0 ? (
            <p>No applications found.</p>
          ) : (
            <table border="1" cellPadding="10" style={{ borderCollapse: "collapse", width: "100%", marginTop: "1rem" }}>
              <thead>
                <tr>
                  <th>Job Title</th>
                  <th>Applicant</th>
                  <th>Email</th>
                  <th>Status</th>
                  <th>Applied On</th>
                  <th>Resume</th>
                </tr>
              </thead>
              <tbody>
                {applications.map((app) => (
                  <tr key={app.id}>
                    <td>{app.job?.title || "N/A"}</td>
                    <td>{app.user?.name || "Anonymous"}</td>
                    <td>{app.user?.email || "N/A"}</td>
                    <td>{app.status}</td>
                    <td>{new Date(app.created_at).toLocaleString()}</td>
                    <td>
                      {app.resume_url ? (
                        <a href={app.resume_url} target="_blank" rel="noopener noreferrer">
                          View Resume
                        </a>
                      ) : (
                        "N/A"
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </>
      )}
    </div>
  );
}

