import React, { useEffect, useState } from "react";
import API from "../api";
import { useNavigate } from "react-router-dom";

function AdminDashboard() {
  const [jobs, setJobs] = useState([]);
  const [form, setForm] = useState({ title: "", description: "", location: "", salary: "", status: "Active"});
  const navigate = useNavigate();

  const fetchJobs = async () => {
    try {
      const res = await API.get("/jobs/");
      setJobs(res.data);
    } catch (err) {
      if (err.response?.status === 401) {
        navigate("/login");
      }
    }
  };

//   useEffect(() => {
//     fetchJobs();
//   }, []);

useEffect(() => {
  fetchJobs();
}, [fetchJobs]); // âœ… add fetchJobs here

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleAddJob = async (e) => {
    e.preventDefault();

    const payload = {
      ...form,
      salary: parseFloat(form.salary), // ðŸ‘ˆ Ensure salary is a float
    };
    console.log("Payload to backend:", payload);
    try {
      await API.post("/jobs/", payload);
      setForm({ title: "", description: "", location: "", salary: "", status: "Active"});
      fetchJobs();
    } catch (err) {
      alert("Failed to add job");
    }
  };

  const handleDelete = async (id) => {
    try {
      await API.delete(`/jobs/${id}`);
      fetchJobs();
    } catch (err) {
      alert("Failed to delete job");
    }
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h2>Admin Dashboard</h2>

      <h3>Add Job</h3>
      <form onSubmit={handleAddJob} style={{ maxWidth: 600 }}>
        <input name="title" value={form.title} onChange={handleChange} placeholder="Title" required />
        <br />
        <textarea name="description" value={form.description} onChange={handleChange} placeholder="Description" required />
        <br />
        <input name="location" value={form.location} onChange={handleChange} placeholder="Location" required />
        <br />
        <input type="number" name="salary" value={form.salary} onChange={handleChange} placeholder="Salary" required />
        <br />
        <button type="submit">Add Job</button>
      </form>

      <h3>All Jobs</h3>
      {jobs.map((job) => (
        <div key={job.id} style={{ margin: "1rem 0", padding: "1rem", border: "1px solid gray" }}>
          <h4>{job.title}</h4>
          <p>{job.location} | â‚¹{job.salary}</p>
          <p>{job.description}</p>
          <p>Status: {job.status}</p>
          <button onClick={() => handleDelete(job.id)}>Delete</button>
        </div>
      ))}
    </div>
  );
}

export default AdminDashboard;
