import { useNavigate } from "react-router-dom";
import { FaUserShield, FaUser, FaUserPlus } from "react-icons/fa";

export default function HomePage() {
  const navigate = useNavigate();

  const styles = {
    container: {
      minHeight: "100vh",
      display: "flex",
      flexDirection: "column",
      justifyContent: "center",
      alignItems: "center",
      background: "linear-gradient(-45deg, #f0f4f8, #d9e4f5, #e1f5fe, #dceefb)",
      backgroundSize: "400% 400%",
      animation: "gradientBG 15s ease infinite",
      fontFamily: "'Segoe UI', sans-serif",
      padding: "20px",
      textAlign: "center",
    },
    heading: {
      fontSize: "3rem",
      marginBottom: "0.5rem",
      color: "#333",
    },
    subheading: {
      fontSize: "1.2rem",
      marginBottom: "2.5rem",
      color: "#555",
    },
    button: {
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      gap: "10px",
      padding: "12px 24px",
      margin: "10px",
      fontSize: "1rem",
      backgroundColor: "#007bff",
      color: "#fff",
      border: "none",
      borderRadius: "8px",
      cursor: "pointer",
      transition: "all 0.3s",
      width: "220px",
    },
    buttonHover: {
      backgroundColor: "#0056b3",
      transform: "scale(1.05)",
    },
    logo: {
      width: "80px",
      marginBottom: "20px",
    },
    styleTag: `
      @keyframes gradientBG {
        0% {background-position: 0% 50%;}
        50% {background-position: 100% 50%;}
        100% {background-position: 0% 50%;}
      }
      @media (max-width: 600px) {
        h1 { font-size: 2rem !important; }
        p { font-size: 1rem !important; }
        button { font-size: 0.9rem !important; width: 90% !important; }
      }
    `,
  };

  const handleHover = (e, isHovering) => {
    e.target.style.backgroundColor = isHovering
      ? styles.buttonHover.backgroundColor
      : styles.button.backgroundColor;
    e.target.style.transform = isHovering
      ? styles.buttonHover.transform
      : "scale(1)";
  };

  return (
    <div style={styles.container}>
      {/* Inline keyframe animation and media query */}
      <style>{styles.styleTag}</style>
      <style>
          {`
            body {
              overflow: hidden;
            }
          `}
        </style>

      {/* Optional logo (if available) */}
      {/* <img src="/logo.png" alt="Job Board Logo" style={styles.logo} /> */}

      <h1 style={styles.heading}>üëã Welcome to Job Board</h1>
      <p style={styles.subheading}>Your gateway to exciting career opportunities üöÄ</p>

      <button
        style={styles.button}
        onMouseOver={(e) => handleHover(e, true)}
        onMouseOut={(e) => handleHover(e, false)}
        onClick={() => navigate("/login-admin")}
      >
        <FaUserShield />
        Login as Admin
      </button>

      <button
        style={styles.button}
        onMouseOver={(e) => handleHover(e, true)}
        onMouseOut={(e) => handleHover(e, false)}
        onClick={() => navigate("/login-user")}
      >
        <FaUser />
        Login as User
      </button>

      <button
        style={styles.button}
        onMouseOver={(e) => handleHover(e, true)}
        onMouseOut={(e) => handleHover(e, false)}
        onClick={() => navigate("/register")}
      >
        <FaUserPlus />
        Register
      </button>
    </div>
  );
}


