import React, { useEffect, useState } from "react";
import axios from "axios";

const MyApplications = () => {
  const [applications, setApplications] = useState([]);

  useEffect(() => {
    const fetchApplications = async () => {
      try {
        const token = localStorage.getItem("token");
        console.log("üîê Retrieved token from localStorage:", token);

        if (!token) {
          console.warn("‚ö†Ô∏è No token found in localStorage. User might not be logged in.");
          return;
        }

        const res = await axios.get("http://localhost:8000/applications/", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        console.log("‚úÖ Fetched applications successfully:", res.data);

        setApplications(res.data);
      } catch (error) {
        console.error("‚ùå Failed to fetch applications:", error);
        if (error.response) {
          console.error("üîç Server responded with error:", error.response.data);
        }
      }
    };

    fetchApplications();
  }, []);

  console.log("üì¶ Final applications state:", applications);

  return (
    <div>
      <h2>My Applications</h2>
      {applications.length === 0 ? (
        <p>üö´ No applications submitted yet.</p>
      ) : (
        applications.map((app, index) => (
          <div
            key={app.id || index}
            style={{ border: "1px solid #ccc", marginBottom: "10px", padding: "10px" }}
          >
            <p><strong>Job Title:</strong> {app.job?.title || "N/A"}</p>
            <p><strong>Status:</strong> {app.status || "N/A"}</p>
            <p><strong>Applied on:</strong> {app.created_at ? new Date(app.created_at).toLocaleString() : "Unknown"}</p>
          </div>
        ))
      )}
    </div>
  );
};

export default MyApplications;
